/* eslint-disable */
import React, { Component } from 'react';
import { Container, Row, Col } from 'react-bootstrap';
import {
  getGameListData,
  getGenreListData,
} from '../../actions/rawg-api';
import CategoryList from './CategoryList';
import GameItem from '../landing_page/game_list/game_item';
import LoadingSpinner from '../shared/loading_spinner';
import '../../scss/browse/browse.scss';

export default class BrowsePage extends Component {
  constructor(props) {
    super(props);

    this.state = {
      // id: props.location.state.categoryId,
      // name: props.location.state.categoryName,
      genreList: [],
      gameList: [],
    };

    console.log(props.match.params.category);
  }

  async componentDidMount() {
    const genreResponse = await getGenreListData();
    // const genreGameList = await getGenreGamesListData(this.state.id, 'released');
    this.setState({
      genreList: genreResponse.map((res) => ({ id: res.id, name: res.name })),
      gameList: await getGameListData(this.props.match.params.category, 'released'),
    });
    // console.log('genre game list', genreGameList);
  }

  selectCategory = (id, name) => {
    console.log(this.state.id, this.state.name);
    console.log('select', id, name);
  };

  render() {
    if (!this.state.gameList.length) {
      return <LoadingSpinner />;
    }

    const galleryData = this.state.gameList.map((game) => (
      <GameItem game={game} />
    ));

    return (
      <Container className="w-100 py-5" id="browse-container">
        <Row>
          <Col xs={{ order: 2 }} lg={{ span: 10, order: 1 }}>
            <div className="pb-2">
              <p>Browsing {this.state.name} Games</p>
            </div>
            <div className="d-flex flex-wrap align-content-center">
              {galleryData}
            </div>
          </Col>
          <Col xs={{ span: 12, order: 1 }} lg={{ span: 2, order: 2 }}>
            <CategoryList
              genreList={this.state.genreList}
              selectCategory={this.selectCategory}
            />
          </Col>
        </Row>
      </Container>
    );
  }
}
