/* eslint-disable */

import React, { Component } from 'react';
import { Container, Row, Col } from 'react-bootstrap';
import { getGameListData, getGenreListData } from '../../actions/rawg-api';
import CategoryListItem from './CategoryListItem';
import '../../scss/browse_page/browse_page.scss';

export default class BrowsePage extends Component {
  constructor(props) {
    super(props);

    this.state = {
      id: props.location.state.categoryId,
      name: props.location.state.categoryName,
    };
    console.log('browsepage state id and name', this.state.id, this.state.name);
  }

  async componentDidMount() {
    console.log('browse mounted', this.props.location.state);
    const genreResponse = await getGenreListData();
    this.setState({
      genreList: genreResponse.map((res) => ({ id: res.id, name: res.name })),
    });
    // const games = getGameListData('genre', 'adventure');
    // console.log(games);
  }

  // selectCategory(category) {
  //   this.setState({
  //     selectedCategoryId: category
  //   })
  // }

  render() {
    const dropdownItems = genreList.map((genre) => (
      <NavbarDropdownItem key={genre.id} id={genre.id} name={genre.name} />
    ));

    return (
      <Container className="w-100 py-5" id="browse-container">
        <Row>
          <Col xs={{ order: 2 }} lg={{ order: 1 }}>
            <div className="pb-2">
              <p>Sort By: Release Date</p>
            </div>
            <p>games here</p>
          </Col>
          <Col xs={{ span: 12, order: 1 }} lg={2}>
            <div className="bottom-border pb-2">
              <p>Filters</p>
            </div>
            <div className="mt-3">
              <CategoryListItem />
            </div>
          </Col>
        </Row>
      </Container>
    );
  }
}
