/* eslint-disable */
import React, { useState, useEffect } from 'react';
import ReactDom from 'react-dom';
import { Form, InputGroup, FormControl } from 'react-bootstrap';
import config from '../../config';
import search from '../../assets/search.svg';
import '../../scss/navbar.scss';

require('es6-promise').polyfill();
require('isomorphic-fetch');

export default function Searchbar() {
  const [data, setData] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');

  // saves what the user is typing in the search field
  const handleChange = event => {
    setSearchTerm(event.target.value);
    console.log('searchterm', searchTerm);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    alert(`Submitting search ${searchTerm}`);
    }

  const searchDatabase = (searchTerm) => {
    alert('it works');
    console.log('searchterm in func', searchTerm);
    fetch(`https://rawg-video-games-database.p.rapidapi.com/games?search=${searchTerm ? searchTerm : ''}`, {
      headers: {
        'x-rapidapi-key': config.RAWG_API_KEY,
        'x-rapidapi-host': 'rawg-video-games-database.p.rapidapi.com',
      }
    })
      .then((res) => res.json())
      .then((json) => setData(json))
      .catch((error) => {
        console.log('Request failed', error);
      });
      console.log('data set')
  }

  console.log('data', data);

  useEffect(() => {
    // listens for enter to submit search
    const listener = event => {
      if (event.code === "Enter" || event.code === "NumpadEnter") {
        alert('enter pressed');
        event.preventDefault();
        handleSubmit(event);
        searchDatabase(searchTerm);
      }
    };
    document.addEventListener("keydown", listener);
    return () => {
      document.removeEventListener("keydown", listener);
    };
  }, []);

  return (
    <Form inline id="form-id">
      <InputGroup>
        <InputGroup.Prepend>
          <InputGroup.Text id="searchbar-icon">
            <img src={search} alt="search icon" />
          </InputGroup.Text>
        </InputGroup.Prepend>
        <FormControl
          type="text"
          placeholder="Search"
          className=" mr-sm-2"
          id="navbar-search"
          value={searchTerm}
          onChange={handleChange}
        />
      </InputGroup>
    </Form>
  );
}
