import React, { Component } from 'react';
import { Container, Row, Col } from 'react-bootstrap';
import LoadingSpinner from '../../shared/loading_spinner';
import '../../../scss/individual_page/individual_page_game_info.scss';

export default class GameInfoPage extends Component {
  constructor(props) {
    super(props);

    this.state = {
      game: null,
    };

    console.log('state game', this.state.game);
  }

  async componentDidMount() {
    await this.props.game;
    if(this.props.game) {
      this.setState({
        game: this.props.game
      })
    }
  }

  render() {
    if (!this.props.game) {
      return <LoadingSpinner />;
    }

    return (
      <Container className="pt-5" id="game-info-container">
        <Row>
          <Col lg={2} className="about-game">
            About Game
          </Col>
          <Col lg={{ span: 9, offset: 1 }}>
            <Row className="pb-3">
              <Col>
                <h5>Developer</h5>
                <p>developer</p>
              </Col>
              <Col>
                <h5>Publisher</h5>
              </Col>
              <Col>
                <h5>Available</h5>
              </Col>
            </Row>
            <Row>
              <Col>
                <h5>Tags</h5>
              </Col>
              <Col>
                <h5>Rating</h5>
              </Col>
              <Col>
                <h5>Platform</h5>
              </Col>
            </Row>
          </Col>
        </Row>
      </Container>
    );
  }
}
