import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { Navbar, Nav, Form, FormControl, InputGroup } from 'react-bootstrap';
import { getGenreList } from '../../actions/rawg-api';
import NavbarDropdown from './NavbarDropdown';
// import axios from 'axios';

import logo from '../../assets/logo.svg';
import search from '../../assets/search.svg';

import '../../scss/navbar.scss';

export default class NavBar extends Component() {
  constructor(props) {
    super(props);

    this.state = {
      list: [],
    };
  }

  async componentDidMount() {
    this.setState({
      list: await getGameList(this.props.queryCategory, this.props.queryItem),
    });
  }



  render() {
    return (
      <>
        <Navbar
          collapseOnSelect
          expand="lg"
          variant="dark"
          className="navbar-scss"
        >
          <Navbar.Brand as={Link} to="/">
            <img src={logo} alt="logo" id="store-logo" />
          </Navbar.Brand>
          <Navbar.Brand
            as={Link}
            to="/"
            className="d-none d-xl-block"
            id="store-name"
          >
            Game Nation
          </Navbar.Brand>
          <Navbar.Brand
            as={Link}
            to="/account"
            alt="account icon"
            className="d-block d-md-none"
          >
            {accountIcon}
          </Navbar.Brand>
          <Navbar.Brand
            as={Link}
            to="/wishlist"
            alt="favorites icon"
            className="d-block d-md-none"
          >
            {favoritesIcon}
          </Navbar.Brand>
          <Navbar.Brand
            as={Link}
            to="/cart"
            alt="cart icon"
            className="d-block d-md-none"
          >
            {cartIcon}
          </Navbar.Brand>
          <Navbar.Toggle aria-controls="responsive-navbar-nav" />
          <Navbar.Collapse id="responsive-navbar-nav">
            <Nav className="mr-auto">
              <Nav.Link as={Link} to="/store" className="mx-1">
                Store
              </Nav.Link>
              <NavbarDropdown />
            </Nav>
            <Form inline>
              <InputGroup>
                <InputGroup.Prepend>
                  <InputGroup.Text id="searchbar-icon">
                    <img src={search} alt="search icon" />
                  </InputGroup.Text>
                </InputGroup.Prepend>
                <FormControl
                  type="text"
                  placeholder="Search"
                  className=" mr-sm-2"
                  id="navbar-search"
                />
              </InputGroup>
            </Form>
            <Navbar.Brand
              as={Link}
              to="/account"
              alt="account icon"
              className="d-none d-lg-block ml-4"
            >
              {accountIcon}
            </Navbar.Brand>
            <Navbar.Brand
              as={Link}
              to="/wishlist"
              alt="favorites icon"
              className="d-none d-lg-block ml-2"
            >
              {favoritesIcon}
            </Navbar.Brand>
            <Navbar.Brand
              as={Link}
              to="/cart"
              alt="cart icon"
              className="d-none d-lg-block ml-1"
            >
              {cartIcon}
            </Navbar.Brand>
          </Navbar.Collapse>
        </Navbar>
      </>
    );
  }
}
