/* eslint-disable */

import React, { Component } from 'react';
import { Container, Row, Col } from 'react-bootstrap';
import { getGameListData, getGenreListData } from '../../actions/rawg-api';
import CategoryList from './CategoryList';
import GameItem from '../landing_page/game_list/game_item';
import LoadingSpinner from '../shared/loading_spinner';
import '../../scss/browse_page/browse_page.scss';

export default class BrowsePage extends Component {
  constructor(props) {
    super(props);

    this.state = {
      id: props.location.state.categoryId,
      name: props.location.state.categoryName,
      genreList: [],
      gameList: [],
    };
  }

  async componentDidMount() {
    const genreResponse = await getGenreListData();
    this.setState({
      genreList: genreResponse.map((res) => ({ id: res.id, name: res.name })),
      gameList: await getGameListData('genre', 'adventure'),
    });
    console.log('browse mount game res', this.state.gameList);
  }

  // selectCategory(category) {
  //   this.setState({
  //     selectedCategoryId: category
  //   })
  // }

  render() {
    if (!this.state.gameList.length) {
      return <LoadingSpinner />;
    }

    const galleryData = this.state.list.map((game) => <GameItem game={game} />);

    return (
      <Container className="w-100 py-5" id="browse-container">
        <Row>
          <Col xs={{ order: 2 }} lg={{ order: 1 }}>
            <div className="pb-2">
              <p>Sort By: Release Date</p>
            </div>
            <div className="d-flex">
              {galleryData}
            </div>
          </Col>
          <Col xs={{ span: 12, order: 1 }} lg={2}>
              <CategoryList genreList={this.state.genreList} />
          </Col>
        </Row>
      </Container>
    );
  }
}
